import react, {Component} from 'react';
import styles from '../styles/molecules/main.module.css';

import Sidebar from '../components/molecules/Sidebar';
import ContentStateManagement from '../components/molecules/ContentStateManagement';
import Footer from '../components/molecules/Footer';
import withRedux from "next-redux-wrapper";
import makeStore from '../components/redux/reduxComponent';

class StateManagement extends Component{

  getInitialProps({store, isServer, pathname, query}){
    // component will read from store's state when rendered
    store.dispatch({type: 'FOO', payload: 'foo'});
    // pass some custom props to component from here
    return {custom: 'custom'}; 
  };

  componentDidMount(){
    console.log('value of StateManagement styles: ', styles)
  }

  render(){
    return(
      <div className='height100Width100'>
        <div className={styles.gridContainerMain}>
          <div className={styles.gridSidebarMain}>
            <Sidebar/>
          </div>
          <div className={styles.gridContentMain}>
            <ContentStateManagement
              reduxExample={foo}
            />
          </div>
          <div className={styles.gridFooterMain}>
            <Footer/>
          </div>
        </div>
      </div>
    );
  }
}

StateManagementRedux = withRedux(makeStore, (state) => (
  {foo: state.foo}
))(StateManagement);

export default StateManagementRedux;
