import react, {Component} from 'react';

import ChildExample from '../quarks/ChildExample';

import styles from '../../styles/molecules/main.module.css';
import buttonStyle from '../../styles/quarks/button.module.css';


class ContentStateManagement extends Component{

  state={
    counter: 0, 
    childCounter: 0,
    inputVariable: '', 
    foo: ''
  }

  componentDidMount(){
    console.log("inside the contentStateManagement component componentDidMount")
  }

  componentDidUpdate(prevProps, prevState){
    if(prevProps.foo!=this.props.foo){
      this.setState({foo: JSON.stringify(this.props.foo)})
    }
  }

  childCounterFn=(counter)=>{
    this.setState({childCounter: counter})
  }

  render(){

    return(
      <div className={styles.mainContainer}>
        <div
          style={{
            textDecoration: 'underline', 
            fontWeight: '900', 
            width: '100%', 
            textAlign: 'center', 
            fontSize: '2rem'
          }}
        >
          State Management
        </div>
        <div
          style={{
            textDecoration: 'underline', 
            fontWeight: '900', 
            width: '100%', 
            textAlign: 'left',
            margin: '1rem', 
            fontSize: '1.2rem'
          }}
        >
          Increment/Decrement
        </div>
        <div
          style={{
            textDecoration: 'none', 
            fontWeight: '900', 
            width: '100%', 
            textAlign: 'left',
            margin: '1rem', 
            fontSize: '1.2rem'
          }}
        >
          <div
            style={{
              display: 'flex', 
              flexDirection: 'row', 
              width: '100%', 
              fontSize: '3rem'
            }}
          >
            <div style={{flex: 1}}></div>
            <div
              className={[styles.noselect, buttonStyle.button, buttonStyle.fadeIn].join(" ")}
              style={{
                flex: 1, 
                textAlign: 'center', 
                border: '1px black solid'
              }}
              onClick={()=>{this.setState({counter: this.state.counter-1})}}
            >
              -
            </div>
              <div
                style={{
                  flex: 2,
                  fontSize: '3rem',
                  textAlign: 'center'
                }}
              >
                {this.state.counter}
              </div>
              <div
                className={[styles.noselect, buttonStyle.button, buttonStyle.fadeIn].join(" ")}
                style={{
                  flex: 1, 
                  textAlign: 'center', 
                  border: '1px black solid'
                }}
                onClick={()=>{this.setState({counter: this.state.counter+1})}}
              >
                +
              </div>
              <div style={{flex: 1}}></div>
          </div>
        </div>
        <div
          style={{
            textDecoration: 'underline', 
            fontWeight: '900', 
            width: '100%', 
            textAlign: 'left',
            margin: '1rem', 
            fontSize: '1.2rem'
          }}
        >
          Input Variable
        </div>
        <div
          style={{
            textDecoration: 'none', 
            fontWeight: '900', 
            width: '100%', 
            textAlign: 'left',
            margin: '1rem', 
            fontSize: '1.2rem'
          }}
        >
          <div
            style={{
              display: 'flex', 
              flexDirection: 'row', 
              width: '100%', 
              fontSize: '2rem'
            }}
          >
            <div 
              style={{
                flex: 1, 
                fontSize: '1rem', 
                lineHeight: '2rem'
              }}
            >
            <input
              type="text" 
              id="myText" 
              placeholder="Enter some text..."
              value={this.state.inputVariable}
              onChange={(e)=>{this.setState({inputVariable: e.target.value})}}
              ></input>
            </div>
            <div 
              style={{
                flex: 5, 
                lineHeight:'2rem'
              }}
            >
              {this.state.inputVariable}
            </div>
          </div>
        </div>
        <div
          style={{
            textDecoration: 'underline', 
            fontWeight: '900', 
            width: '100%', 
            textAlign: 'left',
            margin: '1rem', 
            fontSize: '1.2rem'
          }}
        >
          Nested Component Inheritance
        </div>
        <div
          style={{
            textDecoration: 'none', 
            fontWeight: '900', 
            width: '100%', 
            textAlign: 'left',
            margin: '1rem', 
            fontSize: '1.2rem'
          }}
        >
          <ChildExample
            childCounter={this.state.childCounter}
            childCounterFn={(counter)=>{this.childCounterFn(counter)}}
          />
        </div>
        <div
          style={{
            textDecoration: 'underline', 
            fontWeight: '900', 
            width: '100%', 
            textAlign: 'left',
            margin: '1rem',
            fontSize: '1.2rem'
          }}
        >
          Redux
        </div>
        <button onClick={(e)=>{
          console.log('button has been clicked') 
          this.props.handleSubmit(e)
        }}>
          Load
        </button>
        <div>
          {this.state.foo==""?
            null:(
              this.state.foo[0]
            )  
          }
        </div>
      </div>
    );
  }
}

export default ContentStateManagement;
