  }

  render(){
    return(
      <div className={styles.button}>
        {this.props.key?(
          <div>
            <div
              onMouseEnter={() => this.setState({showExpl: this.props.key}, ()=>{
                console.log('set value of this.state.showExpl to: ', this.state.showExpl)
              })}
              onMouseLeave={() => this.setState({showExpl: -1})}
            >
              {this.props.item}
            </div>
            <div>
              {this.state.showExpl!=-1?(
                <div>
                  {this.props.listItemExpl[this.state.showExpl]}
                </div>
              ):(
                null
              )}
            </div>
          </div>
        ):(
          <div>
            {this.props.item}
          </div>
        )}
      </div>
    )
  }
}

export default Button;
