import react, {Component} from 'react';
//import styles from '../../styles/molecules/content.module.css';
import styles from '../../styles/molecules/main.module.css';

class ContentStateManagement extends Component{

  state={
    counter: 0
  }

  buttonStyle=Object.assign({}, styles);

  componentDidMount(){
    console.log("inside the contentStateManagement component componentDidMount")
    console.log('value of buttonStyle: ', this.buttonStyle)
  }

  render(){

    return(
      <div className={styles.mainContainer}>
        <div
          style={{
            textDecoration: 'underline', 
            fontWeight: '900', 
            width: '100%', 
            textAlign: 'center', 
            fontSize: '2rem'
          }}
        >
          State Management
        </div>
        <div
          style={{
            textDecoration: 'underline', 
            fontWeight: '900', 
            width: '100%', 
            textAlign: 'left',
            margin: '1rem', 
            fontSize: '1.2rem'
          }}
        >
          Increment/Decrement
        </div>
        <div
          style={{
            textDecoration: 'none', 
            fontWeight: '900', 
            width: '100%', 
            textAlign: 'left',
            margin: '1rem', 
            fontSize: '1.2rem'
          }}
        >
          <div
            style={{
              display: 'flex', 
              flexDirection: 'row', 
              width: '100%', 
              fontSize: '3rem'
            }}
          >
            <div style={{flex: 1}}></div>
            <div
              className={this.buttonStyle}
              style={{
                flex: 1, 
                textAlign: 'center', 
                border: '1px black solid'
              }}
              onClick={()=>{this.setState({counter: this.state.counter-1})}}
            >
              -
            </div>
              <div
                className={Object.assign({}, styles.noselect)}
                style={{
                  flex: 2,
                  fontSize: '3rem',
                  textAlign: 'center'
                }}
              >
                {this.state.counter}
              </div>
              <div
                className={Object.assign({}, styles.noselect)}
                style={{
                  flex: 1, 
                  textAlign: 'center', 
                  border: '1px black solid'
                }}
                onClick={()=>{this.setState({counter: this.state.counter+1})}}
              >
                +
              </div>
              <div style={{flex: 1}}></div>
          </div>
        </div>
        <div
          style={{
            textDecoration: 'underline', 
            fontWeight: '900', 
            width: '100%', 
            textAlign: 'left',
            margin: '1rem', 
            fontSize: '1.2rem'
          }}
        >
          Input Variable
        </div>
        <div
          style={{
            textDecoration: 'underline', 
            fontWeight: '900', 
            width: '100%', 
            textAlign: 'left',
            margin: '1rem', 
            fontSize: '1.2rem'
          }}
        >
          Redux
        </div>
      </div>
    );
  }
}

export default ContentStateManagement;
